-----------------------------TABLE-CREATION-----------------------------
create extension if not exists "uuid-ossp";

begin;

drop type if exists product_type cascade;
drop table if exists products cascade;
drop table if exists stocks cascade;

create type product_type as enum ('SingleVariantProduct');
create table products ( 
    id uuid primary key default uuid_generate_v4(),
    __typename product_type not null,
    product_code text not null,
    title text not null,
    description text,
    price integer not null,
    slug text not null,
    primary_image text not null,
    base_img_url text not null,
    override_url text default null,
    variant jsonb not null 
);
create table stocks ( 
	product_id uuid primary key default uuid_generate_v4(),
	count INTEGER not null
);

alter table products add foreign key ("id") references "stocks" ("product_id") deferrable initially deferred;
alter table stocks add foreign key ("product_id") references "products" ("id") deferrable initially deferred;

commit;


